import React from "react";

class BackgroundImage extends React.Component {

     

state = {
    src : "../assets/clear_sky.jpg",
    IdRange : this.props.IdRange
    }

     getWeatherImage({IdRange}) {
        switch(true) {
            case IdRange>=200 && IdRange<=232:
                this.setstate({
                    src : "../assets/thunderstorm.jpg"
                });
                break;
            case IdRange>=300 && IdRange<=321:
                    this.setstate({
                        src : "../assets/drizzle.jpg"
                    });
                break;
            case IdRange>=500 && IdRange<=531:
                    this.setstate({
                        src : "../assets/rain.jpg"
                    });
                break;
            case IdRange>=600 && IdRange<=622:
                    this.setstate({
                        src : "../assets/snow.jpg"
                    });
            case IdRange>=701 && IdRange<=781:
                    this.setstate({
                        src : "../assets/atmosphere.jpg"
                    });
                break;
            case IdRange==800:
                    this.setstate({
                        src : "../assets/clear_sky.jpg"
                    });
                break;
            case IdRange>=801 && IdRange<=804:
                    this.setstate({
                        src : "../assets/clouds.jpg"
                    });
                break;
        }
    }

    render () {
        return (
         <div>
             <img className="img-fluid" src = {this.state.src} />
         </div>
        );
    }

}

export default BackgroundImage;
