import React from 'react';

import './weather_component_style.css';

const Weather = (props) => {

     const range=props.id;
     switch (true) {
         case range >=200 &&range <232:
             document.getElementsByClassName('container').style.backgroundImage="url('../assets/thunderstorm.jpg')";
             break;
        default:
                document.getElementsByClassName('container').style.backgroundImage="url('../assets/aii_weather_image.jpg')";

     }

    return (
        <div className = "container">
           <div className = "cards pt-4">
               <h1>
                   {props.city}
                   {props.country?(","):null}
                   {props.country}
               </h1>

               {props.temp_celsius?(
                   <h1 className = "py-2">{props.temp_celsius}&deg;</h1>
               ):null}
               {minmaxTemperature(props.temp_min,props.temp_max)}
               <h4 className = "py-3">{props.description}</h4>
               <h4 className = "py-1"> 
               <span className="px-3">
               {props.humidity?("Humidity:"):null}
              {props.humidity}
              </span>
              <span className="px-3">
              {props.pressure?("Pressure:"):null}
               {props.pressure}
               </span>
               </h4>
           </div>
        </div>
    );
};

function minmaxTemperature(min,max) {
    if(min && max){
    return (
        <h3>
            <span className = "px-4">min:{min}&deg;</span>
            <span className = "px-4">max:{max}&deg;</span>
        </h3>
    );
    }
}

export default Weather;
