import React from 'react';

import './weather_component_style.css';


const Weather = (props) => {

    {get_weatherBackground(props.id)}

    return (
        <div className = "container"> 
           <div className = "cards pt-4">
               <h1>
                   {props.city}
                   {props.country?(","):null}
                   {props.country}
               </h1>

               {props.temp_celsius?(
                   <h1 className = "py-2">{props.temp_celsius}&deg;</h1>
               ):null}
               {minmaxTemperature(props.temp_min,props.temp_max)}
               <h4 className = "py-3">{props.description}</h4>
               <h4 className = "py-1"> 
               <span className="px-3">
               {props.humidity?("Humidity:"):null}
              {props.humidity}
              </span>
              <span className="px-3">
              {props.pressure?("Pressure:"):null}
               {props.pressure}
               </span>
               </h4>
           </div>
        </div>
    );
};

function minmaxTemperature(min,max) {
    if(min && max){
    return (
        <h3>
            <span className = "px-4">min:{min}&deg;</span>
            <span className = "px-4">max:{max}&deg;</span>
        </h3>
    );
    }
}


function get_weatherBackground(id) {
    switch(true) {
        case id>=200 && id<=232:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/thunderstorm.jpg')";
          break;
        case id>=300 && id<=321:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/drizzle.jpg')";
          break;
        case id>=500 && id<=531:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/rain.jpg')";
          break;
         case id>=600 && id<=632:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/snow.jpg')";
          break;
         case id>=701 && id<=781:
             <script>
              document.getElementsByClassName('container').style.backgroundImage = "url('./assets/atmosphere.jpg')";
             </script>
          break;
          case id==800:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/clear_sky.jpg')";
          break;
          case id>=801 && id<=804:
          document.getElementsByClassName('container').style.backgroundImage = "url('./assets/clouds.jpg')";
          break;
    }
}

export default Weather;
