import React from 'react';

class PresentDate extends React.Component {
    state = {
        curTime: null
    }
    componentDidMount() {
        setInterval( () => {
            this.setState({
                curTime : new Date().toLocaleString()
            })
        },1000)
    }

    render() {
        return (
            <div>
                {this.state.curTime}
            </div>
        )
    }

}

export default PresentDate;
