import React from "react";

class BackgroundImage extends React.Component {

     

state = {
    src : "../assets/clear_sky.jpg",
    alt : undefined,
    Idrange : this.props.IdRange
    }
     getWeatherImage() {
         const ID = this.state.Idrange;
        switch(true) {
            case ID>=200 && ID<=232:
                this.setstate({
                    src : "../assets/thunderstorm.jpg",
                    alt : "weatherimage"
                });
                break;
            case ID>=300 && ID<=321:
                    this.setstate({
                        src : "../assets/drizzle.jpg"
                    });
                break;
            case ID>=500 && ID<=531:
                    this.setstate({
                        src : "../assets/rain.jpg"
                    });
                break;
            case ID>=600 && ID<=622:
                    this.setstate({
                        src : "../assets/snow.jpg"
                    });
            case ID>=701 && ID<=781:
                    this.setstate({
                        src : "../assets/atmosphere.jpg"
                    });
                break;
            case ID==800:
                    this.setstate({
                        src : "../assets/clear_sky.jpg"
                    });
                break;
            case ID>=801 && ID<=804:
                    this.setstate({
                        src : "../assets/clouds.jpg"
                    });
                break;
        }
    }

    render () {
        return (
         <div>
             <img className="img-fluid" src = {this.state.src} />
         </div>
        );
    }

}

export default BackgroundImage;
