import React from 'react';

import './weather_component_style.css';

const Weather = (props) => {

    return (
        <div className = "container">
           <div className = "cards pt-4">
               <h1>
                   {props.city}
                   {props.country?(","):null}
                   {props.country}
               </h1>

               {props.temp_celsius?(
                   <h1 className = "py-2">{props.temp_celsius}&deg;</h1>
               ):null}
               {minmaxTemperature(props.temp_min,props.temp_max)}
               <h4 className = "py-3">{props.description}
               {props.city? <img className="img-fluid" src={`http://openweathermap.org/img/w/${props.icon}.png`}/>:null}
               </h4>
               <h4 className = "py-1"> 
               <span className="px-3">
               {props.humidity?("Humidity:"):null}
              {props.humidity}
              </span>
              <span className="px-3">
              {props.pressure?("Pressure:"):null}
               {props.pressure}
               </span>
               </h4>
           </div>
        </div>
    );
};

function minmaxTemperature(min,max) {
    if(min && max){
    return (
        <h3>
            <span className = "px-4">min:{min}&deg;</span>
            <span className = "px-4">max:{max}&deg;</span>
        </h3>
    );
    }
}

function getWeatherImage(IdRange) {
    switch(true) {
        case IdRange>=200 && IdRange<=232:
            <img src={"../assets/thunderstorm.jpg"}/>
            break;
        case IdRange>=300 && IdRange<=321:
            <img src={"../assets/drizzle.jpg"}/>
            break;
        case IdRange>=500 && IdRange<=531:
            <img src={"../assets/rain.jpg"}/>
            break;
        case IdRange>=701 && IdRange<=781:
            <img src={"../assets/snow.jpg"}/>
            break;
        case IdRange==800:
            <img src={"../assets/clear_sky.jpg"}/>
            break;
        case IdRange>=801 && IdRange<=804:
            <img src={"../assets/clouds.jpg"}/>
            break;
    }
}


export default Weather;
